import React, { useState } from 'react';
import { View, Text, StyleSheet, Button } from 'react-native';

const CounterScreen = function() {
  //useState will initialize the default/starting value of state. [] are array destructuring. 
  // Anytime we want to change the state, we dont modify the counter variable directly. Instead, we will call setCounter
  const [counter, setCounter] = useState(0);

  return (
  <View>
    <Button title="Increase" onPress={() => {
      //Dont do this: counter++;
      console.log(counter)

    }}/>
    <Button title="Decrease" onPress={() => {
      counter--;
      console.log(counter)
    }}/>
    <Text>Current Count:</Text>
  </View>
  );
};

const styles = StyleSheet.create({});

export default CounterScreen;
